import React, { Component } from 'react';
import './App.css';
// import DataList from './../dataList'

class MainComponent extends Component {
  constructor (props) {
    super(props)
    this.state = {
      response: '',
      post: '',
      responseToPost: '',
      keyword: ''
    }
  }

  componentWillMount () {
    this.props.getCurrentCity()
  }

  // componentWillReceiveProps (nextProps) {
  //   if (nextProps.location.city !== this.props.location.city) this.setState({ keyword: nextProps.location.city })
  // }

  handleInputChange = (e) => {
    this.setState({ keyword: e.target.value })
  }

  submit = () => {
    if (this.state.keyword) {
      this.props.openLoading()
      if (/\d/.test(this.state.keyword)) {
        console.log(`${this.state.keyword} is a number`)
        this.props.getBudget(this.props.location.ip ,this.state.keyword)
      } else {
        this.props.mainSearch(this.props.location.ip ,this.state.keyword)
      }
    } else {
      alert('ใส่ข้อมูลให้ครบถ้วน')
    }
  }

  _handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      console.log('do validate');
    }
  }

  clear () {
    this.setState({ keyword: '' })
  }

  render() {
    // console.log(this.props.sky)
    return (
      <div className="App">
        <header className="App-header">
        เที่ยวไหนถูก?
        </header>
        <div className='App-body'>
          <div className='first_topic'>
            {
              this.props.location.city ? `ต้นทางของคุณ คือ ${this.props.location.city} (${this.props.location.ip})`: `กำลังหาต้นทางของคุณ. . . `
            }
          </div>
          <div>
            {/* <DataList
              id='input-search'
              option={[1,'Bangkok',3,4,5,6,7,8,9,10]}
              styleContainer={'st-datalist-container-car'}
              styleInput={'search-input'}
              placeholder='ระบุงบประมาณ หรือ ชื่อประเทศ'
              value={this.state.keyword}
              onInputChange={this.handleInputChange.bind(this)}
              /> */}
            <input
            type="text"
            className={'search-input'}
            placeholder='ระบุงบประมาณ หรือ ชื่อ เมือง'
            value={this.state.keyword}
            onChange={this.handleInputChange.bind(this)}
            onKeyPress={this._handleKeyPress}
            />
            <button className="search-btn" onClick={this.submit.bind(this)} >
            หาเลย
            </button>
          </div>
          <div>
            {this.props.action.onLoading ? (
              'กำลังค้นหาข้อมูล'
            ) : '' }
          </div>
        </div>
        <div>
        </div>
      </div>
    );
  }
}

export default MainComponent;
