import React, { Component } from 'react'

import './dataList.css'

export default class DataList extends Component {
  constructor (props) {
    super(props)
    this.state = {
      initialArray: this.props.option,
      currentArray: this.props.option,
      value: this.props.value,
      isChangeOpt: true
    }
  }

  onChangeInput (e) {
    let noOpt = document.getElementById('no-option')
    this.props.onInputChange(e.target.value)
    let str = (e.target.value).toLowerCase()
    let curPart = this.state.initialArray
    let newPart = curPart.filter(x =>
      x ? new RegExp(str).test(x.toLowerCase()) : ''
    )
    if (!newPart.length) {
      noOpt.style.display = 'block'
    } else {
      noOpt.style.display = 'none'
    }
    this.setState({ currentArray: newPart, value: e.target.value, isChangeOpt: false })
  }

  componentWillReceiveProps (nextProps) {
    if (nextProps.option !== this.props.option && this.state.isChangeOpt) {
      this.setState({
        initialArray: nextProps.option,
        currentArray: nextProps.option
      })
    }
    if (nextProps.value !== this.props.value) {
      this.setState({ value: nextProps.value })
    }
  }

  act (e) {
    if (e.type === 'focus') {
      document.getElementById(this.props.styleContainer).style.display = 'block'
      document.getElementById(this.props.id).value = ''
      // this.setState({ value: '' })
    } else if ((e.type === 'blur')) {
      document.getElementById(this.props.styleContainer).style.display = 'none'
      document.getElementById('no-option').style.display = 'none'
      this.setState({ currentArray: this.state.initialArray })
    }
  }

  getDatalist (e) {
    // this.props.onInputChange(e.target.textContent)
    this.setState({ value: e.target.textContent })
    document.getElementById(this.props.styleContainer).style.display = 'none'
  }

  render () {
    return (
      <div className={this.props.styleContainer} onBlur={this.act.bind(this)} >
        <input
          className={this.props.styleInput}
          id={this.props.id}
          placeholder={this.props.placeholder}
          onChange={this.onChangeInput.bind(this)}
          onFocus={this.act.bind(this)}
          value={this.state.value}
          />
        <div className="st-datalist" id={this.props.styleContainer}>
          <div className="st-datalist-option"
            style={{display: 'none'}}
            id='no-option'>ไม่พบข้อมูล</div>
          {
            (this.state.currentArray ? this.state.currentArray.slice(0, 20) : []).map((x, i) =>
            <div key={i}
              className="st-datalist-option"
              value={`${x}`}
              onMouseDown={this.getDatalist.bind(this)}>
              {x}
            </div>
            )
          }
        </div>
      </div>
    )
  }
}
